import React from "react";

function InputArea(){

    function addItem(){
        setItems(prev=>{
          return [...prev,inputText];
        });
        setInputText("");
      }
    return (
        <div className="form">
        <input type="text" onChange={updateInputText} value={inputText}/>
        <button onClick={addItem}>
          <span>Add</span>
        </button>
      </div>
    )
}

export default InputArea;
